function apprise(string, description='', args, callback) {
    var default_args = {'confirm': false, 'verify': false, 'input': false, 'animate': false}
    if (args) {
        for (var index in default_args) {
            if (typeof args[index] == "undefined")args[index] = default_args[index];
        }
    }
    var aHeight = $(document).height();
    var aWidth = $(document).width();
    $('body').append('<div class="appriseOverlay" id="aOverlay"></div>');
    $('.appriseOverlay').css('height', aHeight).css('width', aWidth).fadeIn(100);
    $('body').append('<div class="modal" style="display: block"><div class="modal-dialog"><div class="appriseOuter modal-content"></div></div></div>');
    $('.appriseOuter').append('<div class="modal-header text-center"><h4 class="modal-title appriseHeading"></h4></div>');
    $('.appriseHeading').append(string);
    if(description !== '') {
        $('.appriseOuter').append('<div class="modal-body appriseInner"></div>');
        $('.appriseInner').append(description);
    }
    $('.appriseOuter').append('<div class="aButtons modal-footer"></div>');

    if (args) {
        if (args['confirm'] || args['input']) {
            $('.aButtons').append('<button value="ok" class="btn btn-success">Yes</button>');
            $('.aButtons').append('<button value="cancel" class="btn btn-danger">Cancel</button>');
        }
        else if (args['verify']) {
            $('.aButtons').append('<button value="ok" class="btn btn-success">Yes</button>');
            $('.aButtons').append('<button value="cancel" class="btn btn-danger">No</button>');
        }
        else {
            $('.aButtons').append('<button value="ok" class="btn btn-success">Ok</button>');
        }
    }
    else {
        $('.aButtons').append('<button value="ok" class="btn btn-success">Ok</button>');
    }
    $(document).keypress(function (e) {
        if ($('.appriseOverlay').is(':visible')) {
            if (e.keyCode == 13) {
                $('.aButtons > button[value="ok"]').click();
            }
        }
    });
    var aText = false;
    $('.aTextbox').keyup(function () {
        aText = $(this).val();
    });
    $('.aButtons > button').click(function () {
        $('.appriseOverlay').remove();
        $('.appriseOuter').remove();
        $('.modal:visible').hide();
        if (callback) {
            var wButton = $(this).attr("value");
            if (wButton == 'ok') {
                callback(true);
            }
            else if (wButton == 'cancel') {
                callback(false);
            }
        }
    });
}